app.get('/main-table', function (req, res) {
  sql.open(connectionString, function (err, conn) {
    if (err) {
      console.error('Error occurred:', err);
      res.status(500).send('Database connection error');
      return;
    }
    var query = "\n    SELECT produk.ID_PRODUK, \n    produk.NAMA_PRODUK, \n    produk_detail.URL, \n    spec_server.IP_SERVER, \n    karyawan.NAMA, \n    status.NAMA_STATUS \n    FROM produk \n    INNER JOIN produk_detail ON produk.ID_PRODUK = produk_detail.PRODUK_ID \n    INNER JOIN spec_server ON produk_detail.SERVER = spec_server.ID_SPEC_SERVER \n    INNER JOIN karyawan ON produk_detail.PIC_NIPPOS = karyawan.NIPPOS \n    INNER JOIN status ON produk.FLAG_STATUS = status.ID_STATUS";
    conn.query(query, function (err, results) {
      if (err) {
        console.error('Error executing query:', err);
        res.status(500).send('Query execution error', err);
      } else {
        res.json(results);
      }
      conn.close();
    });
  });
});
