    var query = "\n    INSERT INTO penempatan(NAMA_PENEMPATAN) VALUES ('".concat(NEW_NAMA_PENEMPATAN, "');");
    console.log("query", query);
    conn.query(query, function (err, results) {
      if (err) {
        console.error('Error executing query:', err);
