app.post('/pemberitahuan', (req, res) => {
  sql.open(connectionString, (err, conn) => {
    if (err) {
      console.error('Error occurred:', err);
      res.status(500).send('Database connection error');
      return;
    }

    const nippos = req.body.nippos;
    console.log('nippos', nippos);

