    const query = `
      SELECT 
        account.EXP_DATE_PASSWORD, 
        account.JENIS_AKUN, 
        spec_server.IP_SERVER,
        produk.NAMA_PRODUK,
        karyawan.NIPPOS
      FROM produk_detail
      INNER JOIN produk ON produk_detail.ID_PRODUK_DETAIL = produk.ID_PRODUK
      INNER JOIN spec_server ON produk_detail.ID_PRODUK_DETAIL = spec_server.PRODUK_DETAIL_ID
